import React, { useState } from 'react';
import MealEditForm from './MealEditForm';
import MealForm from './MealForm';

const MealList = ({ meals, onDelete, onEdit }) => {
  const [editingMeal, setEditingMeal] = useState(null);

  const handleUpdate = (updatedMeal) => {
    fetch(`https://meal-planner-app-backend.onrender.com/meals/${updatedMeal.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedMeal),
    })
      .then((response) => response.json())
      .then(() => {
        onEdit(updatedMeal); // Use the onEdit prop from Meals.js
        setEditingMeal(null);
      })
      .catch((error) => console.error('Error updating meal:', error));
  };

  const handleCancelEdit = () => {
    setEditingMeal(null);
  };

  return (
    <div className="meal-list-container">
      <h2>Meal List</h2>

      {editingMeal ? (
        <MealEditForm
          meal={editingMeal}
          onUpdate={handleUpdate}
          onCancel={handleCancelEdit}
        />
      ) : (
        <MealForm onAddMeal={() => {}} /> // Add meal functionality is handled in Meals.js
      )}

      <div className="meal-list">
        {meals.map((meal) => (
          <div key={meal.id} className="meal-card">
            <h3>{meal.name}</h3>
            <p><strong>Ingredients:</strong> {meal.ingredients}</p>
            <p><strong>Instructions:</strong> {meal.instructions}</p>
            {meal.image_url && <img src={meal.image_url} alt={meal.name} className="meal-image" />}
            <div className="meal-buttons">
              <button onClick={() => onDelete(meal.id)} className="delete">Delete</button>
              <button onClick={() => setEditingMeal(meal)} className="edit">Edit</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MealList;
